long CommonData::eval_time(struct timeval start, struct timeval end) {
    long mtime, seconds, useconds;
    seconds  = end.tv_sec  - start.tv_sec;
    useconds = end.tv_usec - start.tv_usec;
    mtime = ((seconds) * 1000 + useconds/1000.0) + 0.5;
    //cout<<"Elapsed time: "<<mtime<<" milliseconds\n";
    return mtime;
}

void CommonData::print_state(pt_state st) {
    for (int i = 0; i < 16; i++) {
        if (i % 4 == 0) printf(" ");
        printf("%02x", st[i]);
    }
    printf("\n");
}

void CommonData::print_state_u8(u8 *st) {
    for (int i = 0; i < 16; i++) {
        if (i % 4 == 0) printf(" ");
        printf("%02x", st[i]);
    }
    printf("\n");
}

void CommonData::printVec(long *vec, int dim) {
    for (int i = 0; i < dim; i++) {
        std::cout << vec[i];
    }
    std::cout << std::endl;
}